    Dictionary<string, string> PathToBreadCrumb(string path)
    {
        var Out = new Dictionary<string, string>();
        string s = "";
        if (path != null)
        {
            foreach (var item in path?.Split(System.IO.Path.DirectorySeparatorChar))
            {
                if (!string.IsNullOrWhiteSpace(item))
                {
                    s += item;
                    s += "%5C";
                    Out.Add($"{s}", item);
                }
            }
        }
        return Out;
    }


