                    }
                    else
                    {
                        fp = Core.CreateAirSharedDir();
                    }
                }

                Path = "explorer?" + Uri.EscapeDataString(fp);
            }

            

            Core.Log($"Logged in {usr.Name} as {usr.Lvl} user");

            NavMan.NavigateTo(Path, false);
        }
        else
